window.addEventListener("scroll",()=>{const e=document.querySelector("nav");window.pageYOffset>100?(e.style.background="rgba(10, 10, 10, 0.95)",e.style.boxShadow="0 5px 20px rgba(0, 0, 0, 0.3)"):(e.style.background="rgba(10, 10, 10, 0.8)",e.style.boxShadow="none")});document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".mobile-menu"),t=document.querySelector(".nav-links");e.addEventListener("click",()=>{t.classList.toggle("active"),t.classList.contains("active")?e.textContent="✕":e.textContent="☰"}),t.querySelectorAll("a").forEach(n=>{n.addEventListener("click",()=>{t.classList.remove("active"),e.textContent="☰"})}),document.addEventListener("click",n=>{n.target.closest("nav")||(t.classList.remove("active"),e.textContent="☰")})});
